local CIRCLE = surface.GetTextureID("SGM/playercircle")local CIROFF = Vector(0,0,1)local CIROF2 = Vector(0,0,10)local CIRDOW = Vector(0,0,80)local CIRANG = Angle(0,0,0)local CIRENA = truefunction GM:SetEnableMawCircle(bool)	CIRENA = boolendhook.Add("PostPlayerDraw","DrawingPlayerCircle",function(pl)	if (!CIRENA) then return end		local p = pl:GetPos()	local t = {		start 	= p+CIROF2,		endpos 	= p-CIRDOW,		filter 	= pl,		mask 	= MASK_SOLID_BRUSHONLY,	}		t = util.TraceLine(t)		local B = MAIN_COLOR.a*1	MAIN_COLOR.a = math.Clamp(70-p:Distance(t.HitPos),0,50)		if (t.Hit) then		cam.Start3D2D(t.HitPos+CIROFF,CIRANG,0.2)			DrawTexturedRect( -100, -100, 200, 200, MAIN_COLOR, CIRCLE)		cam.End3D2D()	end		MAIN_COLOR.a = Bend)